import { useState } from "react";
import axios from "axios";
import { useHistory } from "react-router-dom";

export default function AddDog() {
  const [dog, setDog] = useState({
    name: "",
    height: "",
    weight: "",
    years: "",
    image: "",
    temperaments: [],
  });

  let handleChange = (e) => {
    e.preventDefault();
    setDog({
      ...dog,
      [e.target.name]: e.target.value,
    });
  };

  let history = useHistory();

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post(`http://localhost:3001/api/dogs`, dog).then(() => {
      history.push({
        pathname: "/",
      });
    });
  };

  const alturas = (altura1, altura2) => {
    setDog({
      ...dog,
      height: `${altura1} - ${altura2}`,
    });
  };

  const handleChangeHeight = (e) => {
    let altura1 = `${e.target.value}`;
    alturas(altura1);
  };

  const handleChangeHeight1 = (e) => {
    let altura1 = `${e.target.value}`;
    alturas(altura1);
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>Name: </label>
      <input name="name" value={dog.name} type="text" onChange={handleChange} />
      <br />
      <label>Height: </label>
      <label>Min: </label>
      <input
        name="height"
        value={dog.height}
        type="number"
        onChange={handleChangeHeight}
      />
      <label>Max: </label>
      <input
        name="height"
        value={dog.height}
        type="number"
        onChange={handleChangeHeight1}
      />
    </form>
  );
}
