import {
  FETCH_DOGS,
  SEARCH_DOGS,
  SORT,
  NEXT,
  PREVIOUS,
  WEIGHT_FILTER,
  FILTER_TEMPERAMENTS,
  FETCH_DOGS_TEMP,
} from "../actions";

const initialState = {
  dogs: [],
  filteredDogs: [],
  page: 1,
};

export default function reducer(state = initialState, action) {
  switch (action.type) {
    case FETCH_DOGS:
      return {
        ...state,
        dogs: action.payload,
        filteredDogs: action.payload,
      };
    case FETCH_DOGS_TEMP:
      return {
        ...state,
        dogs: action.payload,
      };
    case SEARCH_DOGS:
      return {
        ...state,
        filteredDogs: { data: action.payload },
      };
    case NEXT:
      return {
        ...state,
        page: state.page + action.payload,
      };
    case PREVIOUS:
      return {
        ...state,
        page: state.page - action.payload,
      };
    case WEIGHT_FILTER:
      if (action.payload === "Heavier") {
        let data = state.filteredDogs.data
          .map((dog) => {
            if (dog.weight) {
              let weightData = dog.weight.split(" - ");
              return { ...dog, weightSort: Number(weightData[0]) };
            } else {
              return { ...dog, weightSort: dog.weight_min };
            }
          })
          .sort((a, b) => {
            if (a.weightSort > b.weightSort) {
              return -1;
            }
            if (a.weightSort < b.weightSort) {
              return 1;
            }
            return 0;
          });
        return {
          ...state,
          filteredDogs: { data: data },
        };
      } else {
        let data = state.filteredDogs.data
          .map((dog) => {
            if (dog.weight) {
              let weightData = dog.weight.split(" - ");
              return { ...dog, weightSort: Number(weightData[0]) };
            } else {
              return { ...dog, weightSort: dog.weight_min };
            }
          })
          .sort((a, b) => {
            if (a.weightSort > b.weightSort) {
              return 1;
            }
            if (a.weightSort < b.weightSort) {
              return -1;
            }
            return 0;
          });
        return {
          ...state,
          filteredDogs: { data: data },
        };
      }
    case SORT:
      let orderedDogs = state.filteredDogs.data.reverse();
      return {
        ...state,
        filteredDogs: { data: orderedDogs },
      };
    case FILTER_TEMPERAMENTS:
      let tempDogs = state.filteredDogs.data.filter((dog) => {
        return dog.temperament.includes(action.payload);
      });
      return {
        ...state,
        filteredDogs: { data: tempDogs },
      };
    default:
      return state;
  }
}
