import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { nextPage, previousPage } from "../../../../store/actions";

export default function Buttons({ page }) {
  let dispatch = useDispatch();
  let dog = useSelector((store) => store.filteredDogs);

  const [boton, setBoton] = useState([]);

  const handleClick = (fn) => {
    dispatch(fn());
  };

  let pagination = (e) => {
    return dog.data.slice(8 * (e.target.value - 1), 8 * e.target.value);
  };

  useEffect(() => {
    let numButtons = () => {
      let num = dog.data && dog.data.length / 8;
      let num1 = Math.ceil(num);
      let arr = new Array(num1);
      for (let i = 0; i < arr.length; i++) {
        arr[i] = i;
      }
      return setBoton(arr);
    };
    dog.data && numButtons();
  }, [dog.data]);

  return (
    <div>
      {dog.data && dog.data.length === 0 ? (
        <div>There are no dogs with these characteristics.</div>
      ) : (
        page
      )}
      <br />
      {page > 1 && (
        <button onClick={() => handleClick(previousPage)}>Previous</button>
      )}
      {dog.data && dog.data.length === 8 && (
        <button onClick={() => handleClick(nextPage)}>Next</button>
      )}
      {boton.map((item) => (
        <button onClick={(e) => pagination(e)}>{item}</button>
      ))}
    </div>
  );
}
