import { useEffect, useState } from "react";
import axios from "axios";
import { useHistory } from "react-router-dom";

export default function AddDog() {
  const [dog, setDog] = useState({
    temperaments: [],
  });

  const [temp, setTemp] = useState([]);

  let handleChange = (e) => {
    e.preventDefault();
    setDog({
      ...dog,
      [e.target.name]: e.target.value,
    });
  };

  let history = useHistory();

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post(`http://localhost:3001/api/dogs`, dog).then(() => {
      history.push({
        pathname: "/",
      });
    });
  };

  useEffect(() => {
    axios("http://localhost:3001/api/temperaments").then((res) =>
      setTemp(res.data)
    );
  }, []);

  const handleCheck = (e) => {
    if (e.target.checked && !dog.diets.includes(e.target.value)) {
      setDog({
        ...dog,
        temperaments: [...dog.temperaments, e.target.value],
      });
    } else if (!e.target.checked) {
      setDog({
        ...dog,
        temperaments: dog.diets.filter((d) => d !== e.target.value),
      });
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>Name: </label>
      <input name="name" value={dog.name} type="text" onChange={handleChange} />
      <br />
      <label>Height: </label>
      <label>Min </label>
      <input
        name="height_min"
        value={dog.height_min}
        type="number"
        onChange={handleChange}
      />
      <label>Max </label>
      <input
        name="height_max"
        value={dog.height_max}
        type="number"
        onChange={handleChange}
      />
      <br />
      <label>Weight: </label>
      <label>Min </label>
      <input
        name="weight_min"
        value={dog.weight_min}
        type="number"
        onChange={handleChange}
      />
      <label>Max </label>
      <input
        name="weight_max"
        value={dog.weight_max}
        type="number"
        onChange={handleChange}
      />
      <br />
      <label>Life span: </label>
      <label>Min </label>
      <input
        name="years_min"
        value={dog.years_min}
        type="number"
        onChange={handleChange}
      />
      <label>Max </label>
      <input
        name="years_max"
        value={dog.years_max}
        type="number"
        onChange={handleChange}
      />
      <br />
      <label>Img: </label>
      <input
        name="image"
        value={dog.image}
        type="text"
        onChange={handleChange}
      />
      <br />
      <label>Temperaments: </label>
      {temp &&
        temp.map((d) => (
          <label htmlFor={d.name} key={d.name}>
            <br />
            <input
              type="checkbox"
              name={d.name}
              value={d.name}
              onChange={handleCheck}
            />
            {d.name}{" "}
          </label>
        ))}
      <br />
      <input type="submit" />
      <br />
    </form>
  );
}
