import { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

export default function DogDetail() {
  const [dog, setDog] = useState(null);
  const { id } = useParams();
  useEffect(() => {
    axios(`http://localhost:3001/api/dogs/${id}`).then((res) =>
      setDog(res.data)
    );
  }, []);

  console.log(dog);
  return (
    <div>
      {dog ? (
        <>
          <h3>Name: {dog[0].name}</h3>
          <img src={dog[0].image} alt="Imagen" />
          <div>Height: {dog[0].height}</div>
          <div>{dog[0].weigh}</div>
          <div>{dog[0].years}</div>
          <div>{dog[0].temperament}</div>
        </>
      ) : (
        <div>Loading...</div>
      )}
    </div>
  );
}
