import { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

export default function DogDetail() {
  const [dog, setDog] = useState(null);
  const { id } = useParams();
  useEffect(() => {
    axios(`http://localhost:3001/api/dogs/${id}`).then((res) =>
      setDog(res.data)
    );
  }, []);

  return (
    <div>
      {dog ? (
        <>
          <h3>{dog.name}</h3>
          <img src={dog.img} alt="Imagen" />
          <h5>{dog.diets}</h5>
          <div>
            {
              <div>
                {" "}
                <b>Type Of Diet:</b>{" "}
                {dog.typeofdiets.map((e) => (
                  <li>{e.name}</li>
                ))}
              </div>
            }
          </div>
          <h4>
            <b>Healt Score: </b>
            {dog.healthScore}
          </h4>
          <div>
            <b>Summary </b>
            <br />
            {dog.summary ? dog.summary.replace(/<[^>]*>/g, "") : "Loading..."}
          </div>
          <br />
          <div>
            <b>Instructions </b>
            <br />
            {dog.instructions}
          </div>
          <br />
        </>
      ) : (
        <div>Loading...</div>
      )}
    </div>
  );
}
