import {
  FETCH_DOGS,
  SEARCH_DOGS,
  SORT,
  NEXT,
  PREVIOUS,
  WEIGHT_FILTER,
} from "../actions";
import { ASCENDENTE } from "../../constantes/sort";

const initialState = {
  dogs: [],
  filteredDogs: [],
  page: 1,
};

export default function reducer(state = initialState, action) {
  switch (action.type) {
    case FETCH_DOGS:
      return {
        ...state,
        dogs: action.payload,
        filteredDogs: action.payload,
      };
    case SEARCH_DOGS:
      return {
        ...state,
        filteredDogs: { data: action.payload },
      };
    case SORT:
      let orderedDogs = [...state.filteredDogs.data];

      orderedDogs = orderedDogs.sort((a, b) => {
        if (a.name < b.name) {
          return action.payload === ASCENDENTE ? -1 : 1;
        }
        if (a.name > b.name) {
          return action.payload === ASCENDENTE ? 1 : -1;
        }
        return 0;
      });
      return {
        ...state,
        filteredDogs: { data: orderedDogs },
      };
    case NEXT:
      return {
        ...state,
        page: state.page + action.payload,
      };
    case PREVIOUS:
      return {
        ...state,
        page: state.page - action.payload,
      };
    case WEIGHT_FILTER:
      if (action.payload === "Heavier") {
        state.filteredDogs &&
          state.filteredDogs.data
            .map((dog) => {
              if (dog.weight) {
                let [weightData] = dog.weight.split(" - ");
                return { ...dog, weightSort: weightData };
              } else {
                return { ...dog, weightSort: dog.weight_min };
              }
            })
            .sort((a, b) => {
              console.log(a, b);
              if (a.weightSort > b.weightSort) {
                return 1;
              }
              if (a.weightSort < b.weightSort) {
                return -1;
              }
              return 0;
            });
      } else {
        return state;
      }
      return {
        ...state,
      };
    default:
      return state;
  }
}
