import { useState } from "react";
import { useDispatch } from "react-redux";
import { api, database, fetchDogs } from "../../store/actions/actions";

export default function FilterDbApi() {
  let dispatch = useDispatch();

  const [color, setColor] = useState({
    active: "",
  });

  const handleClick = (fn, e) => {
    dispatch(fn());
    setColor({
      active: e.target.value,
    });
  };

  return (
    <div className="apidb">
      <button
        onClick={(e) => handleClick(fetchDogs, e)}
        className={color.active === "All" ? "button" : "button_active"}
        id="All"
      >
        All
      </button>
      <button
        onClick={(e) => handleClick(api, e)}
        className={color.active === "Api" ? "button" : "button_active"}
        id="Api"
      >
        Api
      </button>
      <button
        onClick={(e) => handleClick(database, e)}
        className={color.active === "Database" ? "button" : "button_active"}
        id="Database"
      >
        Database
      </button>
    </div>
  );
}
