import { useEffect, useState } from "react";
import axios from "axios";
import { useHistory, useParams } from "react-router-dom";

export default function DogDetail() {
  const [dog, setDog] = useState(null);
  const { id } = useParams();
  useEffect(() => {
    axios(`http://localhost:3001/api/dogs/${id}`).then((res) =>
      setDog(res.data)
    );
  }, []);

  let history = useHistory();

  const onClick = (e) => {
    history.push({
      pathname: "/",
    });
  };

  return (
    <div>
      {dog ? (
        <>
          <h3>{dog[0].name}</h3>
          <img src={dog[0].image} alt="Imagen" />
          <div>Height: {dog[0].height}</div>
          <br />
          <div>Weight: {dog[0].weight}</div>
          <br />
          <div>Years: {dog[0].years}</div>
          <br />
          <div>Temeperament: {dog[0].temperament}</div>
          <br />
          <button onClick={onClick}>Home</button>
        </>
      ) : (
        <div>Loading...</div>
      )}
    </div>
  );
}
